mapper.select:
select
    t1.ID,
    t1.PRICE_CALCULATE_ID,
    t1.DEVICE_NAME,
    t1.ORIGINAL_VALUE,
    t1.USE_LIFE,
    t1.USE_TIME,
    t1.PAY_PRICE,
    t1.IS_DEL,
    t1.CREATETIME,
    t1.CREATEUSER,
    t1.UPDATETIME,
    t1.UPDATEUSER,
    t1.IS_SUBTOTAL

from FORMULA_FIXED_ASSETS_DEPRE t1



        and t1.ID = #{id}


        and t1.PRICE_CALCULATE_ID = #{priceCalculateId}


        and t1.DEVICE_NAME = #{deviceName}


        and t1.ORIGINAL_VALUE = #{originalValue}


        and t1.USE_LIFE = #{useLife}


        and t1.USE_TIME = #{useTime}


        and t1.PAY_PRICE = #{payPrice}


        and t1.IS_DEL = #{isDel}


        and t1.CREATETIME = #{createtime}


        and t1.CREATEUSER = #{createuser}


        and t1.UPDATETIME = #{updatetime}


        and t1.UPDATEUSER = #{updateuser}


        and t1.IS_SUBTOTAL = #{isSubtotal}

select t1.ID, t1.PRICE_CALCULATE_ID, t1.DEVICE_NAME, t1.ORIGINAL_VALUE, t1.USE_LIFE, t1.USE_TIME, t1.PAY_PRICE, t1.IS_DEL, t1.CREATETIME, t1.CREATEUSER, t1.UPDATETIME, t1.UPDATEUSER, t1.IS_SUBTOTAL
from FORMULA_FIXED_ASSETS_DEPRE t1
and t1.ID = #{id}
