mapper.select:
select
    t1.ID,
    t1.PRICE_CALCULATE_ID,
    t1.PARTICIPANT_NAME,
    t1.PARTICIPANT_NUM,
    t1.WORK_HOURS,
    t1.HOUR_WAGE,
    t1.PAY_PRICE,
    t1.IS_DEL,
    t1.CREATETIME,
    t1.CREATEUSER,
    t1.UPDATETIME,
    t1.UPDATEUSER,
    t1.IS_SUBTOTAL

from FORMULA_LABOR t1



        and t1.ID = #{id}


        and t1.PRICE_CALCULATE_ID = #{priceCalculateId}


        and t1.PARTICIPANT_NAME = #{participantName}


        and t1.PARTICIPANT_NUM = #{participantNum}


        and t1.WORK_HOURS = #{workHours}


        and t1.HOUR_WAGE = #{hourWage}


        and t1.PAY_PRICE = #{payPrice}


        and t1.IS_DEL = #{isDel}


        and t1.CREATETIME = #{createtime}


        and t1.CREATEUSER = #{createuser}


        and t1.UPDATETIME = #{updatetime}


        and t1.UPDATEUSER = #{updateuser}


        and t1.IS_SUBTOTAL = #{isSubtotal}

select t1.ID, t1.PRICE_CALCULATE_ID, t1.PARTICIPANT_NAME, t1.PARTICIPANT_NUM, t1.WORK_HOURS, t1.HOUR_WAGE, t1.PAY_PRICE, t1.IS_DEL, t1.CREATETIME, t1.CREATEUSER, t1.UPDATETIME, t1.UPDATEUSER, t1.IS_SUBTOTAL
from FORMULA_LABOR t1
and t1.ID = #{id}
