mapper.select:
select
    t1.ID,
    t1.PRICE_CALCULATE_ID,
    t1.PRODUCT_NAME,
    t1.UNIT,
    t1.PRODUCT_NUM,
    t1.UNIT_PRICE,
    t1.PAY_PRICE,
    t1.IS_DEL,
    t1.CREATETIME,
    t1.CREATEUSER,
    t1.UPDATETIME,
    t1.UPDATEUSER,
    t1.SORT

from FORMULA_MATERIAL_CONSUME t1



        and t1.ID = #{id}


        and t1.PRICE_CALCULATE_ID = #{priceCalculateId}


        and t1.PRODUCT_NAME = #{productName}


        and t1.UNIT = #{unit}


        and t1.PRODUCT_NUM = #{productNum}


        and t1.UNIT_PRICE = #{unitPrice}


        and t1.PAY_PRICE = #{payPrice}


        and t1.IS_DEL = #{isDel}


        and t1.CREATETIME = #{createtime}


        and t1.CREATEUSER = #{createuser}


        and t1.UPDATETIME = #{updatetime}


        and t1.UPDATEUSER = #{updateuser}


        and t1.SORT = #{sort}

select t1.ID, t1.PRICE_CALCULATE_ID, t1.PRODUCT_NAME, t1.UNIT, t1.PRODUCT_NUM, t1.UNIT_PRICE, t1.PAY_PRICE, t1.IS_DEL, t1.CREATETIME, t1.CREATEUSER, t1.UPDATETIME, t1.UPDATEUSER, t1.SORT
from FORMULA_MATERIAL_CONSUME t1
and t1.ID = #{id}
