mapper.select:
select
    t1.ID,
    t1.NOTIFY_APPLY_ID,
    t1.NONLOCAL_PM_PRICE,
    t1.PROJECT_TOTAL_COST,
    t1.IS_DEL,
    t1.CREATETIME,
    t1.CREATEUSER,
    t1.UPDATETIME,
    t1.UPDATEUSER

from FORMULA_PRICE_CALCULATE t1



        and t1.ID = #{id}


        and t1.NOTIFY_APPLY_ID = #{notifyApplyId}


        and t1.NONLOCAL_PM_PRICE = #{nonlocalPmPrice}


        and t1.PROJECT_TOTAL_COST = #{projectTotalCost}


        and t1.IS_DEL = #{isDel}


        and t1.CREATETIME = #{createtime}


        and t1.CREATEUSER = #{createuser}


        and t1.UPDATETIME = #{updatetime}


        and t1.UPDATEUSER = #{updateuser}

select t1.ID, t1.NOTIFY_APPLY_ID, t1.NONLOCAL_PM_PRICE, t1.PROJECT_TOTAL_COST, t1.IS_DEL, t1.CREATETIME, t1.CREATEUSER, t1.UPDATETIME, t1.UPDATEUSER
from FORMULA_PRICE_CALCULATE t1
and t1.ID = #{id}
